
public class If_else_condition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int FirstNumber=100;
	int SecondNumber=200;
	if (FirstNumber>SecondNumber) {
		System.out.println("First number is greater and number is ...>" +FirstNumber);
		
	} else 
	{
		System.out.println("Second number is greater and number is ...>" + SecondNumber);

	}}

}
